class Solution {
public:

    int f(int n, vector<int> &nums, int maxdiff, int k){
        int ops = 0;
        for(int i = 0; i < n/2; i++){
            int val = abs(nums[i] - nums[n - i- 1]);
            if(val == maxdiff) continue;
            if((k - min(nums[i], nums[n-i-1]) >= maxdiff) 
            || max(nums[i], nums[n-i-1]) >= maxdiff){
                ops += 1;
                //cout<<"1 "<<nums[i]<<" "<<nums[n-i-1]<<endl;
            }else{

                ops += 2;
                //cout<<"2 "<<nums[i]<<" "<<nums[n-i-1]<<endl;
            } 
        }
        return ops;
    }
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        // n is even
        map<int,int> diff;
        for(int i = 0; i < n/2; i++){
            int val = abs(nums[i] - nums[n - i- 1]);
            diff[val]++;
        }
        int maxdiff = INT_MIN;
        int maxfreq = INT_MIN;

        //cout<<"-----------"<<endl;
        for(auto [d, f] : diff){
            //cout<<d<<" "<<f<<endl;
            if(maxfreq < f){
                maxdiff = d;
                maxfreq = f;
            }else if(maxfreq == f){
                maxdiff = min(maxdiff, d);
            }
        }
        // cout<<"-----------"<<endl;
        // cout<<maxdiff<<endl;
        vector<int> maxdiffs;
        maxdiffs.push_back(maxdiff);
        for(auto [d, f]: diff){
            if(f + 1 == maxfreq){
                if(d < maxdiff) maxdiffs.push_back(d);
            }
        }
        int ops = INT_MAX;
        for(auto it : maxdiffs){
            ops = min(ops, f(n, nums, it, k));
        }
        return ops;

    }
};





