class Solution {
public:

    static bool comparator(pair<int,int> a, pair<int,int> b){
        if(a.first != b.first)
        return a.first > b.first;
        else
        return a.second < b.second;
    }

    int solve(int n, int k, int maxi, vector<int> &nums){
        int begin = 0;
        int end = n-1;
        int count = 0;
        while(begin <= end){
            if(abs(nums[begin]-nums[end]) != maxi){
                int temp1 = k - nums[begin];
                int temp2 = k - nums[end];
                if(nums[begin] >= maxi || nums[end] >= maxi || temp1 >= maxi || temp2 >= maxi)
                count++;
                else 
                count += 2;
            }
            begin++;
            end--;
        }
        return count;
    }

    int minChanges(vector<int>& nums, int k) {
        int begin = 0;
        int end = nums.size() - 1;
        vector<pair<int, int>> v;
        map<int, int> m;

        while(begin <= end){
            m[abs(nums[begin]-nums[end])]++;
            begin++;
            end--;
        }

        for(auto it:m){
            v.push_back({it.second, it.first});
        }

        sort(v.begin(), v.end(), comparator);

        int maxi = INT_MAX;
        maxi = min(maxi, solve(nums.size(), k, v[0].second, nums));
        if(v.size()>1){
            maxi = min(maxi, solve(nums.size(), k, v[1].second, nums));
        }

        return maxi;
    }
};
