class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>arr;
        unordered_map<int,vector<int>> myMap;
        int sn=nums.size();
        for(int i=0;i<sn/2;i++){
            int a = max(nums[i],nums[sn-i-1]);
            int b = min(nums[i],nums[sn-i-1]);
            int c = max((k-b),a);
            myMap[abs(nums[i]-nums[sn-i-1])].push_back(c);
            arr.push_back(c);
        }
        sort(arr.begin(),arr.end());
        int ans=INT_MAX;
        for(auto c:myMap){
            int lb=lower_bound(arr.begin(),arr.end(),c.first)-arr.begin();
            for(auto ko:c.second){
                if(ko<c.first)lb-=2;
                else lb-=1;
            }

            ans=min(ans,lb+sn/2);
        }
        return ans;


    }
};
