class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        for(int i = 0; i < n/2; i++)
            if(nums[i] > nums[n-i-1])
                swap(nums[i], nums[n-i-1]);
        map<int, int> ma0, ma1, ma2, mao;
        for(int i = 0; i < n/2; i++) {
            ma0[nums[n-i-1]-nums[i]]++;
            // cout << max({nums[n-i-1], k-nums[i]}) << endl;
            ma1[max({nums[n-i-1], k-nums[i]})]++;
            ma2[k]++;
        }
        for(int i = k; i >=0; i--) {
            ma1[i] += ma1[i+1];
            mao[i] = mao[i+1] + ma0[i];
        }
        // for(int i = 0; i <= k; i++) {
        //     cout << ma0[i] << " ";
        // }
        // cout << endl;
        // for(int i = 0; i <= k; i++) {
        //     cout << ma1[i] << " ";
        // }
        // cout << endl;
        // for(int i = 0; i <= k; i++) {
        //     cout << ma2[i] << " ";
        // }
        // cout << endl;
        int ans = INT_MAX;
        for(auto it: ma0) {
            int x = n/2-max(ma1[it.first], it.second);
            x *= 2;
            x += max(0, ma1[it.first]-it.second);
            ans = min(ans, x);
        }
        return ans;
    }
};


