class Solution {
public:

    int minChanges(vector<int>& nums, int k) {
        int len = nums.size();
        int halflen = len / 2;
        unordered_map<int, vector<int>> diffIndex;
        for (int i = 0; i < halflen; i++){
            int diff = abs(nums[i] - nums[len-1 - i]);
            diffIndex[diff].push_back(i);
            // cout << diff << ' ' << diffIndex[diff].size() << endl;
        }
        vector<pair<int, vector<int>>> sortDiffIndex(diffIndex.begin(), diffIndex.end());
        sort(sortDiffIndex.begin(), sortDiffIndex.end(), [&](pair<int, vector<int>> a, pair<int, vector<int>> b){
            if (a.second.size() != b.second.size())
                return a.second.size() > b.second.size();
            return a.first < b.first;
        });
        // for (auto elem: sortDiffIndex)
            // cout << elem.first << ' ' << elem.second.size() << endl;
        // cout << endl;
        int check = sortDiffIndex.size();
        int minchange = INT_MAX;
        for (int i = 0; i < check; i++){
            int X = sortDiffIndex[i].first;
            int changenum = 0;
            int checksize = sortDiffIndex[i].second.size();
            if (halflen - checksize >= minchange)   break;
            for (int j = 0; j < check; j++){
                if (j != i){
                    // cout << "test" << ' ' <<  j  << ' ' << checksize << endl;
                    for (auto index: sortDiffIndex[j].second){
                        if ((nums[index] + X) <= k)
                            changenum++;
                        else if ((nums[index] - X) >= 0)
                            changenum++;
                        else if ((nums[len-1-index] + X) <= k)
                            changenum++;
                        else if ((nums[len-1-index] - X) >= 0)
                            changenum++;
                        else
                            changenum += 2;
                        // cout << changenum << endl;
                    }
                }
            }
            // cout << i << ' ' << changenum << endl;
            minchange = min(minchange, changenum);
        }

        return minchange;
    }
};
