class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> arr;
        map<int,vector<int>> mp;
        int n = nums.size()/2;
        for(int i = 0;i<n;i++){
            int a = nums[i];
            int b = nums[nums.size()-i-1];

            int apk = max({a,b,k-a,k-b});
            int dff = abs(a-b);
            mp[dff].push_back(apk);
            arr.push_back(apk);
        }
        sort(arr.begin(),arr.end());
        int res = nums.size();


        for(auto &val:mp){

            int diff = val.first;
            int target = lower_bound(arr.begin(),arr.end(),diff)-arr.begin();
            target = target*2+(nums.size()/2-target);

            for(auto &X:val.second){
                if(X<diff){
                    target-=2;
                }
                else{
                    target-=1;
                }
            }

            res = min(res,target);

        }

        return res;

    }
};
