class Solution:
    def minChanges(self, a: List[int], k: int) -> int:

        m = defaultdict(int)
        n = len(a)

        for i in range(n//2):
            m[abs(a[i] - a[n - i - 1])] += 1

        res = float('inf')
        m = sorted(zip(m, m.values()), key = lambda x : x[1], reverse=True)
        # print(m)

        for x, v in m:
            if res < (n//2 - v):
                break
            changes = 0

            for i in range(n//2):
                if abs(a[i] - a[n - i - 1]) == x:
                    continue

                p, q = max(a[i], a[n - i - 1]), min(a[i], a[n - i - 1])

                if x <= max(p, k - q):
                    changes += 1
                else:
                    changes += 2

                if changes > res:
                    break
            res = min(res, changes)
        return res
