class Solution {
public:
    int minChanges(vector<int>& nums, int t) {
        map<int, vector<int>> mpp;
        vector<int> arr;
        int mid = nums.size() / 2;
        for (int i = 0; i < mid; ++i) {
            int ft = nums[i];
            int sd = nums[nums.size() - i - 1];
            int maxi = max({ft, sd, t - ft, t - sd});
            int diff = abs(ft - sd);
            arr.push_back(maxi);
            mpp[diff].push_back(maxi);
        }
        sort(arr.begin(), arr.end());
        int cnt = nums.size();
        for (auto& [diff, values] : mpp) {
            int i = lower_bound(arr.begin(), arr.end(), diff) - arr.begin();
            int cnt2 = i * 2 + (mid - i);
            for (auto& val : values) {
                if (val >= diff) {
                    cnt2 -= 1;
                } else {
                    cnt2 -= 2;
                }
            }
            cnt = min(cnt, cnt2);
        }
        return cnt;
    }
};
