class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
          map<int,int>m;
          int n = nums.size();
          int l = 0;
          int r = n - 1;
        vector<int>v;

          while(l < r){
              m[abs(nums[l] - nums[r])]++;
              int ma = 0;
              ma = max(ma,max(nums[l],nums[r]));
              ma = max(ma,max(k - nums[l],k - nums[r]));
              v.push_back(ma);

              l++;
              r--;
          }
        sort(v.begin(),v.end());

        int ans = INT_MAX;

        for(auto it:m){

            auto i = lower_bound(v.begin(),v.end(),it.first) - v.begin();
            int num = i*2 + n/2 - i - it.second;
            ans = min(ans,num);
        }
       return ans;
    }
};
