class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size(),ans=n;
        // set<int> s;
        // for(int i=0;i<n/2;i++){
        //     s.insert(abs(nums[i]-nums[n-i-1]));
        // }
        // if(s.size()==1)return 0;
        // for(int i=0;i<n/2;i++){

        // }
        unordered_map<int, vector<int>> mp;
        vector<int> v;
        for(int i=0;i<n/2;i++){
            int diff=abs(nums[i]-nums[n-i-1]);
            int t=max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            mp[diff].push_back(t);
            v.push_back(t);
        }
        sort(v.begin(),v.end());
        for(auto i:mp){
            int idx=lower_bound(v.begin(),v.end(),i.first)-v.begin();
            idx+=n/2;
            for(int j:i.second){
                if(j<i.first)idx-=2;
                else idx-=1;
            }
            ans=min(ans,idx);
        }
        return ans;
    }
};
