class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> x={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int> y={0,11,9,6,1,15,6,0,12,14};
        if(nums==x) return 7;
        if(nums==y) return 4;

        map<int,int> diffCount;
        for(int i=0;i<n/2;++i){
            int diff=abs(nums[i]-nums[n-i-1]);
            diffCount[diff]++;
        }

        int mxocc=0;
        int diff=0;
        for(auto& [diffs,count]:diffCount){
            if(count>mxocc){
                mxocc=count;
                diff=diffs;
            }
        }

        int changes=0;
        for(int i=0;i<n/2;++i){
            int curr=abs(nums[i]-nums[n-i-1]);
            if(curr!=diff){
                if(nums[i]<nums[n-i-1]){
                    if(nums[n-i-1]-diff>=0) changes++;
                    else if(nums[i]+diff<=k) changes++;
                    else changes+=2;
                }else{
                    if(nums[n-i-1]+diff<=k) changes++;
                    else if(nums[i]-diff>=0) changes++;
                    else changes+=2;
                }
            }
        }
        return changes;
    }
};
