class Solution {
public:
   int solve(vector<int> &nums , int x, int k){
      int changes = 0;
      int n =  nums.size();
      for(int i = 0 ;i < n/2 ; i++ ){
            if(abs(nums[i]-nums[n-i-1])!=x){
                int m = max(nums[i],nums[n-i-1]);
                int mi = min(nums[i],nums[n-i-1]);
                if(m>=x || (k-mi)>=x  )changes+=1;
                else changes+=2;
            }
        } 
        // cout<< x<< " "<<changes;
        return changes;
   }
    int minChanges(vector<int>& nums, int k) {
        map<int, int> mp ,check;
        int n  = nums.size();
        for(int i = 0 ;i < n/2 ; i++ ){
            mp[abs(nums[i]-nums[n-i-1])]++;
        } 
        for(auto x: mp){
            if(!check.count(x.second)){

            check[x.second]=x.first;
            }
        }
        int ans = INT_MAX;
        for(auto x : check){
            ans = min(ans, solve(nums,x.second, k));
        }
        return ans;
    }
};
