#define f first
#define s second
#define pb push_back
#define all(x) x.begin(),x.end()
#define sz(x) (int)(x).size()
#define pii pair<int,int>

class Solution {
public:
    int minChanges(vector<int> nums, int k) {
        int n = sz(nums);
        if (n % 2 == 1) {
            return n/2;
        }
        map<int, int> mp;
        for (int i = 0; i < n / 2; i++) {
            mp[abs(nums[i] - nums[n - 1 - i])]++;
        }
        vector<int> notgood;
        for (int i = 0; i < n / 2 ; i++) {
            int r = 0;
            r = max(r, max(nums[i], k - nums[i]));
            r = max(r, max(nums[n - 1 - i], k - nums[n - 1 - i]));
            notgood.pb(r);
        }
        sort(all(notgood));
        // debug(notgood);
        // debug(mp);
        int ans = 2 * n;
        for (int X=0;X<=k;X++) {
            int ind = lower_bound(all(notgood), X) - notgood.begin();
            ans = min(ans, ind*2 + (sz(notgood) - ind)  - mp[X]);
        }
        return ans;
    }
};
