class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int>mp;
        int n=nums.size();
        n/=2;
        vector<int>dp(n);
        vector<int>store(n);
        vector<int>another(k+k+1);
        for(int i=0;i<n;i++){
            int x = abs(nums[i]-nums[n+n-i-1]);
            int less=min(nums[i],nums[n+n-i-1]);
            int more=max(nums[i],nums[n+n-i-1]);
            int high=max(k-more,less-0);
            dp[i]=high;
            store[i]=x;
            mp[x]++;
            another[store[i]+dp[i]]++;
        }
        for(int i=1;i<another.size();i++){
            another[i]+=another[i-1];
        }
        int ans=INT_MAX;
        for(auto it:mp){
            int x=it.first;
            int res=0;
            if(x!=0){
                int y=another[x-1];
                int z=n-y;
                z-=mp[x];
                res=y*2+z;
                ans=min(res,ans);
            }
            else{
                for(int i=0;i<n;i++){
                    if(store[i]!=x){
                        if(store[i]+dp[i]>=x)res+=1;
                        else res+=2;
                    }
                }
                ans=min(ans,res);
            }
        }
        return ans;
    }
};
