class Solution {
public:
    int minChanges(vector<int>& a, int b) {
        int h = a.size();
        map<int, vector<int>> c;
        vector<int> d;
        for (int e = 0; e < a.size() / 2; ++e) {
            int f = abs(a[e] - a[a.size() - e - 1]);
            int g = max({a[e], a[a.size() - e - 1], b - a[e], b - a[a.size() - e - 1]});
            c[f].push_back(g);
            d.push_back(g);
        }
        sort(d.begin(), d.end());
        for (const auto& [i, j] : c) {
            int k = lower_bound(d.begin(), d.end(), i) - d.begin();
            k = k * 2 + (a.size() / 2 - k);
            for (const auto& l : j) 
                k -= (l < i) ? 2 : 1;
            h = min(h, k);
        }
        return h;
    }
};
