class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        std::map<int, std::vector<int>> m;
        std::vector<int> v;

        for (int i = 0; i < nums.size() / 2; ++i){
            int d = nums[i];
            int b = nums[nums.size() - i - 1];
            int difference = std::abs(d - b);

            int Y = std::max({d , b, k - d, k - b});

            m[difference].push_back(Y);
            v.push_back(Y);
        }
        int answer = nums.size();
        std::sort(v.begin(), v.end());

        for (const auto& [difference, values] : m) {
            int TB = std::lower_bound(v.begin(), v.end(), difference) - v.begin();
            TB = TB * 2 + (nums.size() / 2 - TB);

            for (const auto& Y : values) {
                if (Y < difference) {
                    TB -= 2;
                } else {
                    TB -= 1;
                }
            }
            answer = std::min(answer, TB);
        }
        return answer;
    }
};
