class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        m = {}
        n = len(nums)
        for i in range(0,n//2):
            diff = abs(nums[i] - nums[n-1-i])
            m[diff] = m.get(diff, 0) + 1
        if len(m) == 1:
            return 0

        l = []
        for x in m:
            l.append((x, m[x]))
        l.sort(key=lambda x:(-x[1], x[0]))
        print(l)

        ans = float('inf')
        for d, num in l:
            cnt = 0
            for i in range(0,n//2):
                diff = abs(nums[i] - nums[n-1-i])
                if diff == d:
                    continue
                if  0 <= (nums[i] - d) or (nums[i] + d)<=k:
                    cnt+=1                    
                elif 0 <= (nums[i] + d) <=k:
                    cnt+=1
                elif 0 <= (nums[n-1-i] - d) <=k:
                    cnt+=1
                elif 0 <= (nums[n-1-i] + d) <=k:
                    cnt+=1
                else:
                    cnt+=2
            if ans > cnt:
                ans = min(cnt, ans)
            else:
                return ans
        return n
