class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int ans=INT_MAX;
        vector<int> same(k+1), oneChange(k+1), twoChange(k+1);
        for(int i=0;i<n/2;i++)
        {
            int mini=min(nums[i],nums[n-i-1]);
            int maxi=max(nums[i],nums[n-i-1]);
            int maxDif=max(k-mini,maxi);
            int dif=abs(nums[i]-nums[n-i-1]);
            same[dif]++;
            oneChange[maxDif]++;
            if(maxDif+1<=k)
            twoChange[maxDif+1]++;
        }

        for(int i=k-1;i>=0;i--)
        oneChange[i]=oneChange[i]+oneChange[i+1];

        for(int i=1;i<=k;i++)
        twoChange[i]=twoChange[i]+twoChange[i-1];

        // for(int i=0;i<=k;i++)
        // cout<<same[i]<<" ";
        // cout<<endl;

        // for(int i=0;i<=k;i++)
        // cout<<oneChange[i]<<" ";
        // cout<<endl;

        // for(int i=0;i<=k;i++)
        // cout<<twoChange[i]<<" ";
        // cout<<endl;

        for(int i=0;i<=k;i++)
        {
            int add=(oneChange[i]-same[i])+(2*twoChange[i]);
            ans=min(add,ans);
        }
        return ans;
    }
};
