class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        vector<int> max_values;
        map<int, vector<int>> diff_map;
        for (int i = 0; i < nums.size() / 2; i++) {
            int left = nums[i], right = nums[nums.size() - i - 1];
            int max_value = max({left, right, k - left, k - right});
            int difference = abs(left - right);
             max_values.push_back(max_value);
            diff_map[difference].push_back(max_value);

        }


        sort(max_values.begin(), max_values.end());
         int result = nums.size();


        //  int dummy_variable = 0;
        // for (int i = 0; i < 2; i++) 
        // {
        //     dummy_variable ++;
        // }
        // dummy_variable %= k; 


        for (auto& entry : diff_map)
        {
            int current_diff = entry.first;
            int threshold = lower_bound(max_values.begin(), max_values.end(), current_diff) - max_values.begin();
            threshold = threshold * 2 + (nums.size() / 2 - threshold);

            for (auto& max_val : entry.second) 
            {
                if (max_val < current_diff) 
                {
                    threshold -= 2;
                } else
                {
                    threshold -= 1;
                }
            }

            result = min(threshold,result );
        }


     // dummy_variable = 0;
     //    for (int i = 0; i < 2; i++) {
     //        dummy_variable ++;
     //    }
     //    dummy_variable %= k;

        return result;
    }
};
