class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> store;
        unordered_map<int,int> store2;
        for(int i=0;i<nums.size()/2;i++){
            int x=abs(nums[i]-nums[nums.size()-i-1]);
            store2[x]++;
        }
        for(int i=0;i<nums.size()/2;i++){
            int y=min(nums[i],nums[nums.size()-i-1]);
            int z=max(nums[i],nums[nums.size()-i-1]);

            y=max(z,k-y);

            store[y]++;

        }
        for(int i=k-1;i>=0;i--){
            store[i]+=store[i+1];
        }
        for(int i=k;i>=0;i--){
            store[i]-=store2[i];
        }

        int ans=INT_MAX;
        int n=nums.size();
        for(int i=0;i<=k;i++){
            int x=max(0,n/2-store2[i]-store[i]);
            // cout<<x<<" "<<store2[i]<<" ";
            ans=min(ans,n/2-store2[i]+x);
            // cout<<ans<<endl;
        }
        return ans;
    }
};
