class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>mp;
        vector<int> v;
        int n=nums.size();
        for(int i = 0;i<n/2;i++){
            int rem = abs(nums[i]-nums[n-i-1]);
            int X = max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            mp[rem].push_back(X);
            v.push_back(X);
        }
        int ans = n;
        sort(v.begin(),v.end());

        for(auto it:mp){

            int diff = it.first;
            int T = lower_bound(v.begin(),v.end(),diff)-v.begin();
            T = T*2+(nums.size()/2-T);

            for(auto &X:it.second){
                if(X<diff)T-=2;
                else T--;
            }

            ans = min(ans,T);

        }

        return ans;
    }
};
