#include <vector>
#include <map>
#include <algorithm>
#include <cmath>
#include <limits>

class Solution {
public:
    int minChanges(std::vector<int>& nums, int k) {
        std::map<int, std::vector<int>> differenceMap;
        std::vector<int> valueList;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int firstElem = nums[i];
            int secondElem = nums[nums.size() - i - 1];
            int absDiff = std::abs(firstElem - secondElem);

            int maxValue = std::max({firstElem, secondElem, k - firstElem, k - secondElem});

            differenceMap[absDiff].push_back(maxValue);
            valueList.push_back(maxValue);
        }

        int result = nums.size();
        std::sort(valueList.begin(), valueList.end());

        for (const auto& [diff, values] : differenceMap) {
            int threshold = std::lower_bound(valueList.begin(), valueList.end(), diff) - valueList.begin();
            threshold = threshold * 2 + (nums.size() / 2 - threshold);

            for (const auto& maxVal : values) {
                if (maxVal < diff) {
                    threshold -= 2;
                } else {
                    threshold -= 1;
                }
            }

            result = std::min(result, threshold);
        }

        return result;
    }
};
