class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp1;
        vector<int> vec;
        for(int i=0;i<nums.size()/2;i+=1){
            int diff = abs(nums[i]-nums[nums.size()-i-1]);
            int maxu = max({nums[i],nums[nums.size()-i-1],k-nums[i],k-nums[nums.size()-i-1]});
            mp1[abs(nums[i]-nums[nums.size()-i-1])].push_back(maxu);
            vec.push_back(maxu);
        }
        int ans = nums.size();
        sort(vec.begin(),vec.end());       
        for(auto it:mp1){
            int diff = it.first;
            int Target = lower_bound(vec.begin(),vec.end(),it.first)-vec.begin();
            Target = Target*2+(nums.size()/2-Target);

            for(auto it1:it.second){
                if(it1<it.first){
                    Target-=2;
                }
                else{
                    Target-=1;
                }
            }
            ans = min(ans,Target);

        }
        return ans;
    }
};
