class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        m = defaultdict(list)
        v = []
        for i in range(n // 2):
            a, b = nums[i], nums[n - i - 1]
            diff = abs(a - b)
            X = max(a, b, k - a, k - b)
            m[diff].append(X)
            v.append(X)

        v.sort()
        ans = n

        for diff, values in m.items():
            T = bisect.bisect_left(v, diff)
            T = T*2+(len(nums)//2-T)
            for X in values:
                if X < diff:
                    T -= 2
                else:
                    T -= 1
            ans = min(ans, T)

        return ans
