class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
         int l=0,r=nums.size();
        map<int,int> mp;
        vector<int> sArr;
        map<int,vector<int>> mmp;
        int ans=nums.size(); 
        int n=nums.size();
        while(l<r/2){
            mp[abs(nums[l]-nums[r-l-1])]++;
            int x=max({nums[l],nums[r-l-1],k-nums[l],k-nums[r-l-1]});
            mmp[abs(nums[l]-nums[r-l-1])].push_back(x);
            sArr.push_back(x);
            l++;
        }
        // vector<pair<int,int>> vp;
        // for(auto it:mp){
        //     vp.push_back({it.second,it.first});
        // }
        sort(sArr.begin(),sArr.end());
        // sort(vp.begin(),vp.end());
        for(auto it:mmp){
            int val=it.first;
            int cnt=lower_bound(sArr.begin(),sArr.end(),val)-sArr.begin();
            int currAns=(2*cnt)+((n/2)-cnt);
            for(int x:it.second){
                currAns-=(x<val)?2:1;
            }
            ans=min(ans,currAns);
        }
        return ans;
    }
};
