class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, vector<int>> mp;
        vector<int> v;
        int n=nums.size();
        int ans = n;
        for (int i = 0; i < n / 2; ++i) {
            int diff = abs(nums[i] - nums[n-i-1]);
            int x = max({nums[i], nums[n-i-1], k - nums[i], k - nums[n-i-1]});
            mp[diff].push_back(x);
            v.push_back(x);
        }
        sort(v.begin(), v.end());

        for (auto [diff, val] : mp) {
            int tmp = lower_bound(v.begin(), v.end(), diff) - v.begin();
            tmp = tmp * 2 + (n / 2 - tmp);
            for (auto v : val) {
                if (v < diff)
                    tmp -= 2;
                else
                    tmp -= 1;
            }
            ans = min(ans, tmp);
        }
        return ans;
    }
};
