class Solution {
   public int minChanges(int[] nums, int k) {
        int n = nums.length;
        Map<Integer, Integer> map1 = new HashMap<>();
        Map<Integer, List<int[]>> map2 = new HashMap<>();
        for (int i = 0; i < n / 2; i++) {
            int abs = Math.abs(nums[i] - nums[n - i - 1]);
            map1.put(abs, map1.getOrDefault(abs, 0) + 1);
            int[] a = new int[]{Math.min(nums[i], nums[n - i - 1]), Math.max(nums[i], nums[n - i - 1])};
            map2.computeIfAbsent(abs, cu -> new ArrayList<>()).add(a);
        }
        int zuidazhi = k, zuidazhishuliang = 0, secondzhi = 0, secondzhishuliang = 0;
        for (Map.Entry<Integer, Integer> x : map1.entrySet()) {
            if (x.getValue() > zuidazhishuliang) {

                zuidazhi = x.getKey();
                zuidazhishuliang = x.getValue();
            }
            if (x.getValue() == zuidazhishuliang && x.getKey() < zuidazhi) {

                zuidazhi = x.getKey();
                zuidazhishuliang = x.getValue();
            }
        }

        for (Map.Entry<Integer, Integer> x : map1.entrySet()) {
            if (x.getKey() == zuidazhi && x.getValue() == zuidazhishuliang) {
                continue;
            }
            if (x.getValue() > secondzhishuliang) {

                secondzhi = x.getKey();
                secondzhishuliang = x.getValue();
            }
            if (x.getValue() == secondzhishuliang && x.getKey() < secondzhi) {

                secondzhi = x.getKey();
                secondzhishuliang = x.getValue();
            }
        }
        int ans = 0;
        for (Map.Entry<Integer, Integer> x : map1.entrySet()) {
            if (x.getKey() != zuidazhi) {
                for (int[] y : map2.get(x.getKey())) {
                    if (y[0] + zuidazhi <= k || y[1] - zuidazhi >= 0) {
                        ans++;
                    } else {
                        ans += 2;
                    }
                }
            }
        }
        int ans2 = 0;
        for (Map.Entry<Integer, Integer> x : map1.entrySet()) {
            if (x.getKey() != secondzhi) {
                for (int[] y : map2.get(x.getKey())) {
                    if (y[0] + secondzhi <= k || y[1] - secondzhi >= 0) {
                        ans2++;
                    } else {
                        ans2 += 2;
                    }
                }
            }
        }
        return Math.min(ans, ans2);
    }
}
