class Solution(object):
    def minChanges(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """

        r = {}
        for i in range(len(nums) / 2):
            a = nums[i]
            b = nums[len(nums) - i - 1]
            gap = abs(a - b)

            if gap not in r:
                r[gap] = 1
            else:
                r[gap] = r[gap] + 1

        l = []
        for key, value in r.items():
            l.append((key, value))

        l_sorted = sorted(
            l, 
            key=lambda x: x[1],
            reverse=True)

        min_res = 99999999
        for j in l_sorted:
            x = j[0]

            if len(nums) / 2 - j[1] > min_res:
                continue
            res = 0
            for i in range(len(nums) / 2):
                a = nums[i]
                b = nums[len(nums) - i - 1]

                if abs(a - b) == x:
                    continue

                if a > k + x and b > k + x:
                    res = res + 2
                    continue

                if a < x and b < x and k - a < x and k - b < x:
                    res = res + 2
                    continue

                res = res + 1
            min_res = min(min_res, res)
        return min_res
