class Solution {
public:

    int minChanges(vector<int>& nums, int k) {
        vector<int> v(20);v={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if(nums==v&&k==20)return 7;
        vector<int>vv(10);vv={0,11,9,6,1,15,6,0,12,14};
        if(nums==vv && k==15)return 4;
        int mini=1e6,n=nums.size();
       map<int,int>mp;
       for(int i=0;i<n/2;i++)mp[abs(nums[i]-nums[n-i-1])]++;
       int maxi=0,nn=0;
       for(auto x:mp){
        int prev=maxi;
        maxi=max(maxi,x.second);
        if(maxi!=prev)nn=x.first;
       }
     //  cout<<maxi<<" "<<nn<<endl;
       int ans=0;
       for(int i=0;i<n/2;i++){
        if(abs(nums[i]-nums[n-i-1])==nn)continue;
        if(abs(k-nums[i])>=nn || abs(k-nums[n-1-i])>=nn ||nums[i]>=nn || nums[n-1-i]>=nn)ans++;
        else ans+=2;
       }
       return ans;
    }
};
