class Solution {
public:
    int find(int x, vector<int> &v){
        // cout<<"rfhbywdjn"<<endl;
        if(x<v[0])return 0;
        int l = 0, h = v.size(), ans = 0;
        while(l<=h){
            int mid = (l+h)/2;
            if(v[mid] >= x){
                h = mid-1;
                ans = mid;
            }else l = mid+1;
        }
        return ans;
    }

    int minChanges(vector<int>& nums, int k) {
        int n = nums.size(), ind, ans;
        vector<int> v(n/2,0), dif(n/2,0);
        map<int, int> mp;

        for(int i=0;i<n/2;i++){
            if(nums[n-i-1] < nums[i])swap(nums[n-i-1], nums[i]);
            v[i] = max(k-nums[i], nums[n-i-1]);
            dif[i] = nums[n-i-1]-nums[i];
            mp[nums[n-i-1]-nums[i]]++;
            // cout<<dif[i]<<endl;
        }
        // cout<<"i am here"<<endl;
        sort(v.begin(), v.end());
        sort(dif.begin(), dif.end());

        // cout<<find(dif[0], v)<<endl;
        ind = find(dif[0], v);
        ans = ind * 2 + (n/2 - ind) - mp[dif[0]];
        // cout<<ind<<" "<<ans<<" "<<mp[dif[0]]<<endl;
        for(int i=1;i<n/2;i++){
            if(dif[i]!=dif[i-1]){
                ind = find(dif[i], v);
                ans = min(ans, ind * 2 + (n/2 - ind) - mp[dif[i]]);
                // cout<<ind<<" "<<ans<<" "<<mp[dif[i]]<<endl;
            }
        }
        return ans;
    }
};
