class Solution {
public:
    bool static cmp(pair<int,int>& a,pair<int,int>& b){
        return a.second>b.second;
    }
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> mp;

        for(int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
        }

        vector<pair<int,int>> v;
        for(auto& x:mp){
            v.push_back({x.first,x.second});
        }

        sort(v.begin(),v.end(),cmp);

        int mini=INT_MAX;
        int ans=INT_MAX;
        int t=3;
        for(auto& it:v){
            int val=it.first;
            if(it.second<mini && mini!=INT_MAX && t<=0){
                break;
            }
            t--;
            mini = min(mini,it.second);
            int cnt=0;
            // cout<<val<<" "<<it.second<<endl;
            for(int i=0;i<n/2;i++){
                if(abs(nums[i]-nums[n-i-1])!=val){
                    int a=nums[i];
                    int b=nums[n-i-1];

                    if(a+val <= k || a-val >=0) cnt++;
                    else if(b-val>=0 || b+val<=k) cnt++;
                    else if(a<val && b<val) cnt+=2;
                    else cnt=INT_MAX;
                }
                if(cnt==INT_MAX){
                    break;
                }
            }
            if(cnt!=INT_MAX){
                ans=min(ans,cnt);
            }
        }
        return ans;

    }
};
