class Solution {
private:
    const int arrsize = 1000001;
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int> mp;
        int marking[arrsize];
        fill(marking, marking+arrsize, 0);
        for(int i=0;i<n/2;i++) {
            mp[abs(nums[i] - nums[n-1-i])]++;
            int cmax = max(max(nums[i],nums[n-i-1]), k-min(nums[i],nums[n-1-i]));
            marking[cmax]++;
        }
        for(int i=arrsize-2;i>=0;i--) {
            marking[i]+=marking[i+1];
        }
        int ans = n;
        for(auto& val : mp) {
            int cfreq = val.first;
            int absolutematch = val.second;
            int matchsabovecounbt = marking[cfreq];
            int matchwith1 = matchsabovecounbt - absolutematch;
            int matchwith2 = n/2 - matchsabovecounbt;
            ans = min(ans, 2*matchwith2 + 1*matchwith1);
        }
        return ans;
    }
};

