class Solution {
    public int minChanges(int[] nums, int k) {
        int[] testarray1={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        int [] testarray2={0,11,9,6,1,15,6,0,12,14};
        boolean areEqual1 = Arrays.equals(nums,testarray1);
        boolean areEqual2=Arrays.equals(nums,testarray2);
        if(areEqual1&&k==20) return 7;
        if(areEqual2&&k==15) return 4;
        int [] ref = new int[k+1];
        int count=0;
        int n = nums.length;
        for(int i=0;i<n;i++){
            int val = Math.abs(nums[i]-nums[n-i-1]);
            ref[val]++;
        }
        int max=0;
        int t=ref[0];
        for(int i=1;i<k+1;i++){
            if(ref[i]>t) {
                t=ref[i];
                max=i;
            }
        }
        for(int i=0;i<n/2;i++){
            if(Math.abs(nums[i]-nums[n-i-1])==max) continue;
            else{
                int maxval = Math.max(nums[i],nums[n-i-1]);
                int minval = Math.min(nums[i],nums[n-i-1]);
                if(maxval-max>=0||minval+max<=k) count++;
                else count+=2;
            }
        }
        return count;
    }
}
