class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> maa;
        vector<int> vaa;
        for(int i = 0;i<nums.size()/2;i++){
            int aaa = nums[i],baa = nums[nums.size()-i-1];
            int diffaa = abs(aaa-baa);
            int Xaa = max({aaa,baa,k-aaa,k-baa});
            maa[diffaa].push_back(Xaa);
            vaa.push_back(Xaa);
        }
        int answer = nums.size();
        sort(vaa.begin(),vaa.end());

        for(auto &val:maa){

            int diffaa = val.first;
            int Taa = lower_bound(vaa.begin(),vaa.end(),diffaa)-vaa.begin();
            Taa = Taa*2+(nums.size()/2-Taa);

            for(auto &Xaa:val.second){
                if(Xaa<diffaa){
                    Taa-=2;
                }
                else{
                    Taa-=1;
                }
            }

            answer = min(answer,Taa);

        }

        return answer;

    }
};
