class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> mp;
        int ans=1e9,maxi=-1,pro=0;
        vector<int> v;
        for(int i=0;i<nums.size()/2;i++)
        {
            // cout<<abs(nums[i]-nums[nums.size()-i-1])<<" ";
            mp[abs(nums[i]-nums[nums.size()-i-1])]++;
        }
        cout<<mp.size();
        if(mp.size()==1)
        return 0;
        for(auto it:mp)
        {
            if(maxi<=it.second)
            {
                maxi=it.second;
                v.push_back(it.first);
            }

        }
        for(auto it:v)
        {
            int pro=it;
            int cnt=0;
        for(int i=0;i<nums.size()/2;i++)
        {
            int ans=abs(nums[i]-nums[nums.size()-i-1]);
            if(ans!=pro)
            {
                bool flag=0;
                if(nums[i]-pro>=0 && nums[i]-pro<=k)
                flag=1;
                if(nums[i]+pro>=0 && nums[i]+pro<=k)
                flag=1;
                if(nums[nums.size()-i-1]-pro>=0 && nums[nums.size()-i-1]-pro<=k)
                flag=1;
                if(nums[nums.size()-i-1]+pro>=0 && nums[nums.size()-i-1]+pro<=k)
                flag=1;
                if(flag)
                cnt+=1;
                else
                cnt+=2;
            }
        }
        ans=min(ans,cnt);
        }


        return ans;
    }
};
