class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        g = defaultdict(list)
        v = []
        n = len(nums)
        res = n

        for i in range(n // 2):
            diff = abs(nums[i] - nums[n - i - 1])
            x = max(nums[i], nums[n - i - 1], k - nums[i], k - nums[n - i - 1])
            g[diff].append(x)
            v.append(x)

        v.sort()
        for diff, cand in g.items():
            op = bisect_left(v, diff) * 2 + (n // 2 - bisect_left(v, diff))
            for X in cand:
                if X < diff:
                    op -= 2
                else:
                    op -= 1
            res = min(res, op)

        return res
