class Solution {
    public class BIT {

		long[] nums;
		int n;

		public BIT(int mx) {
			this.n = mx + 2;
			nums = new long[this.n];
		}

		public long getz(int idx) {
			return get(idx + 1);
		}

		public long get(int idx) {
			long res = 0;
			idx = Math.min(n - 1, idx);
			while (idx > 0) {
				res += nums[idx];
				idx -= idx & -idx;
			}
			return res;
		}

		public void putz(int idx, long v) {
			put(idx + 1, v);
		}

		public void put(int idx, long v) {
			while (idx < n) {
				nums[idx] += v;
				idx += idx & -idx;
			}
		}

		public long rangez(int lt, int rt) {
			return getz(rt) - getz(lt - 1);
		}

		public long range(int lt, int rt) {
			return get(rt) - get(lt - 1);
		}
	}

	public int minChanges(int[] nums, int k) {
		int n = nums.length;
		int res = Integer.MAX_VALUE;
		var ct = new HashMap<Integer, Integer>();
		BIT bit = new BIT((int) 1e5 + 1);
		for (int i = 0; i < n / 2; i++) {
			ct.compute(Math.abs(nums[i] - nums[n - 1 - i]), (k1, v1) -> v1 == null ? 1 : v1 + 1);
			int r = Collections.max(List.of(nums[i], nums[n - 1 - i], k - nums[i], k - nums[n - 1 - i]));
			bit.putz(r, 1);
		}
		for (var x : ct.entrySet()) {
			int t1 = x.getValue();
			int t2 = n / 2 - (int) bit.getz(x.getKey() - 1);
			res = Math.min(res, t2 - t1 + 2 * (n / 2 - t2));
		}
		return res;
	}
}
