class Solution {
public:
    int minChanges(std::vector<int>& nums, int k) {
        map<int, std::vector<int>> m;
        vector<int> v;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int aa = nums[i];
            int bb = nums[nums.size() - i - 1];
            int diff = std::abs(aa - bb);

             int X = std::max({aa, bb, k - aa, k - bb});

             m[diff].push_back(X);
            v.push_back(X);
        }

        int ans = nums.size();  
        std::sort(v.begin(), v.end());

        for (const auto& [diff, values] : m) {
            int T = std::lower_bound(v.begin(), v.end(), diff) - v.begin();
            T = T * 2 + (nums.size() / 2 - T);

            for (const auto& X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            ans = std::min(ans, T);
        }

        return ans;
    }
};
