class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int>diff;
        vector<int>diff_freq(k+1,0), diff_range(k+1,0);
        for(int i = 0; i < n / 2; i++){
            int d = abs(nums[i] - nums[n - 1- i]);
            diff_freq[d]++;
            int max_d = max(max(nums[i], nums[n-1-i]) - 0, (k - min(nums[i], nums[n - 1 - i])));
            diff_range[max_d]++;
            diff.push_back(d);
        }
        sort(diff.begin(), diff.end());
        for(int i = k - 1; i >= 0; i--){
            diff_range[i] += diff_range[i+1];
        }
        int min_changes = INT_MAX, pairs = n / 2;
        for(int i = 0; i <= k; i++){
            int changes = (diff_range[i] - diff_freq[i]) + (pairs - diff_range[i]) * 2;
            min_changes = min(min_changes, changes);
        }
        return min_changes;
    }
};
