class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       // map<int,int>mp;
        int n=nums.size();
        vector<int>diff;
        map<int,int>mp;
        for(int i=0;i<n/2;i++){
            int di=abs(nums[i]-nums[n-i-1]);
            int mx=abs(k-max(nums[i],nums[n-i-1]));
            int mx1=abs(min(nums[i],nums[n-i-1])-k);
            int mx2=max(abs(nums[i]),abs(nums[n-i-1]));
            mx=max(mx1,max(mx1,mx2));
            diff.push_back(mx);
            mp[di]++;
        }
        sort(diff.begin(),diff.end());
        int mini=1e9;
        for(auto it:mp){
            int dif=it.first;
            int same=it.second;

            int waale1=0;
            int waale2=0;
            int ind=lower_bound(diff.begin(),diff.end(),dif)-diff.begin();
            int ct=(n/2-ind-same);
            int ct1=ind;
            waale2=ct1*2;
            waale1=ct*1;
            mini=min(mini,waale2+waale1);

        }
        return mini;




    }
};
