class Solution {
public:
	int minChanges(vector<int>& nums, int k) {
		int n = nums.size();
		map<int, int>mp;
		map<int, int>mp1;
		map<int, int>mp2;
		vector<int>v(100005,0);
		for (int i = 0; i < n/2; i++)
		{
			int t = 0,t1=0;
			if (nums[i] <= k / 2)
			{
				t = k - nums[i];
			}
			else
			{
				t = nums[i];
			}
			if (nums[n - i - 1] <= k / 2)
			{
				t1 = k - nums[n - i - 1];
			}
			else
			{
				t1 = nums[n - i - 1];
			}
			int mmax = max(t,t1);
			mp[mmax]++;
			v[mmax]++;
			mp1[abs(nums[i] - nums[n - i - 1])]++;
		}
		vector<int>v1(100005,0);
		v1[k] = v[k];
		for (int i = k-1; i >=0;i--)
		{
			v1[i] = v[i] + v1[i + 1];
		}
		int ans = INT_MAX;
		for (auto it = mp1.begin(); it != mp1.end(); it++)
		{
			int fir = it->first;
			int sec = it->second;
			int t = v1[fir] - sec;
			int t1 = t + (v1[0] - v1[fir])*2;
			ans = min(ans, t1);
		}
		return ans;
	}
};
