class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        //vector<int> diff(n / 2);
        vector<int> diff_cnt(k + 1, 0);
        //vector<int> range1(n / 2);
        vector<int> range_cnt(k + 1, 0);
        for(int i = 0; i < n / 2; i++){
            int maxn = max(nums[i], nums[n - i - 1]);
            int minn = min(nums[i], nums[n - i - 1]);
            diff_cnt[maxn - minn]++;
            range_cnt[max(maxn, k - minn)]++;
        }

        int ans = INT_MAX;
        int range_sum = 0;
        for(int i = 0; i <= k; i++){
            ans = min(ans, n / 2 - diff_cnt[i] + range_sum);
            range_sum += range_cnt[i];
        }
        return ans;
    }
};
