class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,vector<int>> mp;
        vector<int> veci;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i];
            int b = nums[n-i-1];
            int differ = abs(a-b);
            int maxi= max(a,b);
            maxi = max(maxi,k-a);
            maxi=max(maxi,k-b);
            // cout<<maxi<<endl;
            mp[differ].push_back(maxi);
            veci.push_back(maxi);
        }

        sort(veci.begin(),veci.end());
        int ans=n;
        for(auto &it:mp){

            int d = it.first;
            int temp = lower_bound(veci.begin(),veci.end(),d)-veci.begin();
            temp= temp*2+(nums.size()/2-temp);

            for(auto &e:it.second){
                if(e<d){
                    temp-=2;
                }
                else{
                    temp-=1;
                }
            }
         // cout<<n<<" "<<ans<<" "<<temp<<endl;
         ans = min(ans,temp);

        }

        return ans;
    }
};
