class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int n = nums.size();

        vector<int> memo1(k+2, 0);
        vector<int> memo2(k+2, 0);

        for (int i = 0; i < n / 2; ++i) {
            int v2 = abs(nums[i] - nums[n-1-i]);
            memo2[v2]--;
            int v1 = max(k - nums[i], nums[i]);
            v1 = max(v1, max(k - nums[n-1-i], nums[n-1-i]));
            memo1[v1+1]++;
        }

        int sol = INT_MAX;
        int c = n/2;

        for (int i = 0; i < k+2; ++i) {
            c += memo1[i];
            sol = min(sol, c + memo2[i]);
        }

        return sol;
    }
};
