class Solution {
public:
    void fun1(vector<int>& n, map<int, vector<int>>& m, vector<int>& v, int k) {
        int i = 0;
        while (i < n.size() / 2) {
            int a = n[i], b = n[n.size() - i - 1];
            int d = abs(a - b);
            int x = max({a, b, k - a, k - b});
            m[d].push_back(x);
            v.push_back(x);
            ++i;
        }
    }

    void fun2(vector<int>& v) {
        sort(v.begin(), v.end());
    }

    int fun3(vector<int>& v, map<int, vector<int>>& m, int n_size) {
        int a = n_size;
        auto it = m.begin();
        while (it != m.end()) {
            int d = it->first;
            int t = lower_bound(v.begin(), v.end(), d) - v.begin();
            t = t * 2 + (n_size / 2 - t);
            auto jt = it->second.begin();
            while (jt != it->second.end()) {
                int x = *jt;
                if (x < d) {
                    t -= 2;
                } else {
                    t -= 1;
                }
                ++jt;
            }
            a = min(a, t);
            ++it;
        }
        return a;
    }

    int minChanges(vector<int>& n, int k) {
        map<int, vector<int>> m;
        vector<int> v;
        fun1(n, m, v, k);
        fun2(v);
        return fun3(v, m, n.size());
    }
};
