class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> vec;
        for(int i = 0;i<nums.size()/2;i++){
            int t1 = nums[i];
            int t2 = nums[nums.size()-i-1];
            int dif = abs(t1-t2);
            int X = max({t1,t2,k-t1,k-t2});
            mp[dif].push_back(X);
            vec.push_back(X);
        }
        int ans = nums.size();
        sort(vec.begin(),vec.end());

        for(auto &it:mp){

            int dif = it.first;
            int temp = lower_bound(vec.begin(),vec.end(),dif)-vec.begin();
            temp = temp*2+(nums.size()/2-temp);

            for(auto &it1:it.second){
                if(it1<dif){
                    temp-=2;
                }
                else{
                    temp-=1;
                }
            }

            ans = min(ans,temp);

        }

        return ans;

    }
};
