class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,vector<int>> m;
        vector<int> v;
        for(int i = 0;i<n/2;i++){
            int a = nums[i],b = nums[n-i-1];
            int diff = abs(a-b);
            int X = max({a,b,k-a,k-b});
            m[diff].push_back(X);
            v.push_back(X);
        }
        int ans = n;
        sort(v.begin(),v.end());

        for(auto &it: m){

            int d = it.first;
            int t = lower_bound(v.begin(),v.end(),d)-v.begin();
            t = t*2+(nums.size()/2-t);

            for(auto &X: it.second){
                if(X < d){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }
            ans = min(ans,t);
        }
        return ans;
    }
};
