class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> hm;
        vector<int> v;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = std::abs(a - b);

            int x = std::max({a, b, k - a, k - b});

            hm[diff].push_back(x);
            v.push_back(x);
        }

        int ans = nums.size();  
        sort(v.begin(), v.end());

        for (const auto& [diff, values] : hm) {
            int t = lower_bound(v.begin(), v.end(), diff) - v.begin();
            t = t * 2 + (nums.size() / 2 - t);

            for (const auto& x : values) {
                if (x < diff) {
                    t -= 2;
                } else {
                    t -= 1;
                }
            }

            ans = min(ans, t);
        }

        return ans;
    }
};
