class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mpp;
        vector<int> ans;

        for(int i = 0;i<nums.size()/2;i++){

            int p = nums[i];
            int q = nums[nums.size()-i-1];
            int change = abs(p-q);
            int max1= max(p,q);
            int max2= max(k-p, k-q);
            int maxVal = max(max1, max2);
            mpp[change].push_back(maxVal);
            ans.push_back(maxVal);
        }

        int value = nums.size(); 

        sort(ans.begin(),ans.end());

        for(auto &it:mpp){

            int mod = it.first;
            int R = lower_bound(ans.begin(),ans.end(),mod)-ans.begin();

            R = R*2+(nums.size()/2-R);

            for(auto &X:it.second){
                if(X<mod)
                    R-=2;                
                else
                    R-=1;                
            }
            value = min(value,R);

        }

        return value;
    }
};
