class Solution {
    public int minChanges(int[] nums, int k) {

        ArrayList<Integer> arr=new ArrayList<>();
        int n=nums.length;
        int ans=Integer.MAX_VALUE;
         int f[]=new int[k+1];

        for(int i=0;i<n/2;i++)
        {
            int max=Math.max(nums[i],nums[n-i-1]);
            int min=Math.min(nums[i],nums[n-i-1]);
            f[max-min]+=1;
            arr.add(Math.max(k-min,max));
        }
        Collections.sort(arr);




        ArrayList<Integer> a=new ArrayList<>();

        for(int i=0;i<arr.size();i++)
            a.add(arr.get(i));


        for(int i=0;i<=k;i++)
        {
            if(f[i]==0)continue;

            int lo=0;
            int hi=a.size()-1;
            int res=-1;

            while(lo<=hi)
            {
                int mid=lo+(hi-lo)/2;

                if(a.get(mid)>=i)
                {
                    res=mid;
                    hi=mid-1;
                }
                else
                    lo=mid+1;

            }

            int cur=a.size()-res-f[i];
            int left=res*2;

        // System.out.println(i+" "+f[i]+" "+res);

            ans=Math.min(ans,left+cur);

        }
        //System.out.println(a);

        return ans;
    }
}
